import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { userActions } from '../_actions';

import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        this.props.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        if (username && password) {
            this.props.login(username, password);
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { username, password, submitted } = this.state;
        return (
            <Grid container component="main" className={classes.root}>
      <CssBaseline />
      <Grid item xs={false} sm={4} md={7} >
        <img  src={"https://thegooddatafactory.com/images/logo-footer.png"} style={{width: 360,margin : 40}} />
        <h6 style={{margin:60}}>Welcome </h6>
        <p style={{margin:60}}>Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
          Sed id ipsum sit amet metus mattis cursus. Nullam tristique, 
          nibh ut tempus tristique, ligula sem mollis felis, ac luctus nunc purus consectetur sapien. 
          Morbi in nunc pulvinar, scelerisque enim nec, lobortis enim. Aenean non tempor risus. 
          Phasellus vestibulum dapibus libero, quis tristique dui faucibus ac. Etiam lacinia, odio at fermentum volutpat,
           velit metus interdum erat, eu dapibus neque nisl sit amet est. Nam et est ut ipsum pulvinar efficitur vel non massa.
            In gravida nec enim eget consequat. Suspendisse feugiat elit lacus, a posuere ligula convallis in. Duis laoreet
             justo et fermentum suscipit, magna purus egestas ex, vitae elementum arcu dolor sit amet sem.</p>
      </Grid>
      <Grid item xs={12} sm={8} md={5} component={Paper} >
        <div className={classes.paper}>   
          <Typography component="h1" variant="h5">
            Login
          </Typography>
          <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Login</button>
                        {loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                        <Link to="/register" className="btn btn-link">Register</Link>
                    </div>
                </form>
        </div>
      </Grid>
    </Grid>
        );
    }
}

function mapState(state) {
    const { loggingIn } = state.authentication;
    return { loggingIn };
}

const actionCreators = {
    login: userActions.login,
    logout: userActions.logout
};

const connectedLoginPage = connect(mapState, actionCreators)(LoginPage);
export { connectedLoginPage as LoginPage };

const useStyles = makeStyles((theme) => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundColor: '#84ffff',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: 140,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding : 20,
    backgroundColor : '#84ffff',
    borderRadius : 20
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));
